import React, { Component } from 'react';
import Header from "./components/Header"
import Navbar from "./components/Navbar"
import Article from "./components/Article"
import './App.css';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mode: 'welcome',
      selected_article_id: 2,
      header: {title:'WEB', sub:'World Wide Web!'},
      welcome: {title:'Welcome', desc:'Hello, React!!'},
      articles:[
        {id:1, title:'HTML', desc:'HTML is HyperText Markup Language.'},
        {id:2, title:'CSS', desc:'CSS is for desgin.'},
        {id:3, title:'JavaScript', desc:'JavaScript is for interactive.'},
      ]
    }
  }
  render () {
    console.log('App render')
    var _title, _desc = null;
    if (this.state.mode === 'welcome') {
      _title = this.state.welcome.title;
      _desc = this.state.welcome.desc;
    } else if (this.state.mode === 'read') {
      var i = 0;
      while (i < this.state.articles.length) {
        var data = this.state.articles[i];
        if (data.id === this.state.selected_article_id) {
          _title = data.title;
          _desc = data.desc;
          break;
        }
        i = i + 1;
      }
    }
    return (
      <div className="App">
        <Header 
          title={this.state.header.title}
          sub={this.state.header.sub}
          onChangePage={function () {
            this.setState({mode:'welcome'});
          }.bind(this)}
        />
        <Navbar
          onChangePage={function () {
            this.setState({
              mode:'read',
              selected_article_id: 0
            });
          }.bind(this)}
          data={this.state.articles}
        />
        <Article title={_title} desc={_desc}/>
      </div>
    );
  }
}

export default App;
